import React, { useState } from 'react';

export default function RSVPPage({ onSuccess }: { onSuccess: () => void }) {
  const [code, setCode] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (code.trim().toLowerCase() === 'yes2025') {
      onSuccess();
    } else {
      alert('Invalid RSVP code.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center text-center h-screen px-4">
      <h1 className="text-5xl font-bold text-[#6a8a4a] mb-2">Kelly & James</h1>
      <p className="text-xl mb-4">APRIL 12, 2025</p>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          value={code}
          onChange={(e) => setCode(e.target.value)}
          placeholder="Enter RSVP code"
          className="p-2 border border-gray-300 rounded text-center"
        />
        <div>
          <button type="submit" className="bg-[#c9dbc9] hover:bg-[#b8cfb8] text-[#2f3b2f] px-6 py-2 rounded font-semibold">
            RSVP TO ENTER
          </button>
        </div>
      </form>
    </div>
  );
}
