import React, { useState } from 'react';

interface RSVPPageProps {
  onSuccess: () => void;
}

const RSVPPage: React.FC<RSVPPageProps> = ({ onSuccess }) => {
  const [code, setCode] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (code.trim().toLowerCase() === 'yes') {
      onSuccess();
    } else {
      alert('Invalid RSVP code. Please try again.');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen px-4 text-center">
      <div className="bg-white/70 backdrop-blur-md p-10 rounded-xl shadow-xl max-w-md w-full space-y-6 relative z-10">
        <h1 className="text-4xl md:text-5xl font-bold text-[#6a8a4a]">Kelly & James</h1>
        <p className="text-lg tracking-wide text-[#444]">April 12, 2025</p>

        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            placeholder="Enter RSVP code"
            value={code}
            onChange={(e) => setCode(e.target.value)}
            className="w-full px-4 py-2 rounded border border-gray-300 shadow-sm focus:outline-none focus:ring-2 focus:ring-[#6a8a4a]"
          />
          <button
            type="submit"
            className="w-full bg-[#6a8a4a] text-white font-semibold py-2 rounded shadow-md hover:bg-[#59753e] transition"
          >
            RSVP to Enter
          </button>
        </form>
      </div>

      {/* Illustrations - kept subtle */}
      <img
        src="/img/botanical-rose.png"
        alt="Rose Illustration"
        className="absolute top-4 right-4 w-48 opacity-10 pointer-events-none"
      />
      <img
        src="/img/couple-silhouette.png"
        alt="Couple Illustration"
        className="absolute bottom-4 left-4 w-48 opacity-10 pointer-events-none"
      />
    </div>
  );
};

export default RSVPPage;
