import React, { useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import RSVPPage from './pages/RSVPPage';
import WeddingParty from './pages/WeddingParty';
import Details from './pages/Details';
import AboutUs from './pages/AboutUs';
import Travel from './pages/Travel';
import Registry from './pages/Registry';
import FAQ from './pages/FAQ';

function App() {
  const [isRSVPed, setIsRSVPed] = useState(false);

  return (
    <div className="relative bg-gradient-to-br from-[#dce7d6] via-[#fdf4c8] to-[#f7d6dc] min-h-screen font-serif text-[#2f3b2f]">
      <img src="/img/botanical-rose.png" alt="rose" className="absolute top-4 right-4 w-40 opacity-20 pointer-events-none" />
      <img src="/img/couple-silhouette.png" alt="couple" className="absolute bottom-4 left-4 w-40 opacity-10 pointer-events-none" />
      {isRSVPed ? <Navbar /> : null}
      <Routes>
        <Route path="/" element={<RSVPPage onSuccess={() => setIsRSVPed(true)} />} />
        {isRSVPed ? (
          <>
            <Route path="/party" element={<WeddingParty />} />
            <Route path="/details" element={<Details />} />
            <Route path="/about" element={<AboutUs />} />
            <Route path="/travel" element={<Travel />} />
            <Route path="/registry" element={<Registry />} />
            <Route path="/faq" element={<FAQ />} />
          </>
        ) : (
          <Route path="*" element={<Navigate to="/" />} />
        )}
      </Routes>
    </div>
  );
}
export default App;
